mapper.update:
UPDATE GROUP_LIST
   SET GROUP_PROFILE_IMAGE_URL = #{profileImageUrl}
 WHERE GROUP_ID = #{groupId}

mapper.select:
SELECT EXISTS (
	SELECT * 
	  FROM GROUP_MEMBER
	 WHERE GROUP_ID = #{groupId}
	   AND USER_SEQ = #{userSeq}
)

SELECT GROUP_ID as groupId, GROUP_NAME as groupName, GROUP_PROFILE_IMAGE_URL as groupProfileImageUrl, GROUP_LEADER_SEQ as groupLeaderSeq
  FROM GROUP_LIST
 WHERE GROUP_ID = #{groupId}

SELECT COUNT(*)
  FROM GROUP_MEMBER 
 WHERE USER_SEQ = #{userSeq}

mapper.update:
UPDATE GROUP_LIST
   SET GROUP_NAME = #{groupName},
       GROUP_PASSWORD = #{groupPassword}
 WHERE GROUP_ID = #{groupId}

mapper.select:
SELECT GROUP_LEADER_SEQ 
  FROM GROUP_LIST 
 WHERE GROUP_ID = #{groupId}

SELECT GROUP_PASSWORD
  FROM GROUP_LIST 
 WHERE GROUP_ID = #{groupId}

SELECT COUNT(*)
  FROM GROUP_MEMBER
 WHERE GROUP_ID = #{groupId}

SELECT GROUP_LEADER_SEQ as groupLeaderSeq
  FROM GROUP_LIST
 WHERE GROUP_ID = #{groupId}

mapper.update:
UPDATE GROUP_MEMBER
   SET MANAGER_YN = "Y"
 WHERE USER_SEQ = #{userSeq}

UPDATE GROUP_MEMBER
   SET MANAGER_YN = "N"
 WHERE USER_SEQ = #{userSeq}

mapper.delete:
DELETE FROM GROUP_MEMBER 
      WHERE GROUP_ID = #{groupId}
        AND USER_SEQ = #{userSeq}
